    position: fixed;
    top: 0;
    left: 0;
    right: 0;

    transform: translateX(-100%);
    transition: transform 0.3s;

    &:target {
      transform: translateX(0);
    }
  }

  &__section {
    margin-bottom: 80px;

    @include onTablet {
      margin-bottom: 120px;
    }
  }

  &__section-title {
    margin-bottom: 48px;
    font-weight: 500;
    font-size: 32px;
    line-height: 32px;
    text-align: center;
    letter-spacing: -1.5px;

    @include onTablet {
      font-size: 48px;
      line-height: 48px;
    }

    @include onDesktop {
      font-size: 64px;
      line-height: 64px;
    }
  }
}

sources.styles\\utils\\_mixins.scss:
@mixin onTablet {
  @media (min-width: 744px) {
    @content;
  }
}

@mixin onDesktop {
  @media (min-width: 1260px) {
    @content;
  }
}

@mixin top-action {
  --icon-width: 24px;

  display: grid;
  grid-template-columns: 1fr var(--icon-width) var(--icon-width);
  column-gap: 30px;
  justify-items: start;
  align-items: center;

  @include onTablet {
    --icon-width: 32px;
    column-gap: 40px;
  }
}

