sources.styles\\blocks\\grid.scss:
.grid {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  column-gap: 20px;

  @include onTablet {
    grid-template-columns: repeat(6, 1fr);
    column-gap: 24px;
  }

  @include onDesktop {
    grid-template-columns: repeat(12, 1fr);
    column-gap: 24px;
  }

  &--desktop {
    display: block;

    @include onTablet {
      display: grid;
    }

    @include onDesktop {
      display: grid;
    }
  }

  &__item {
    @for $start from 1 through 6 {
      @for $end from $start through 6 {
        &--tablet-#{$start}-#{$end} {
          @include onTablet {
            grid-column: #{$start} / #{$end + 1};
          }
        }
      }
    }

    @for $start from 1 through 12 {
      @for $end from $start through 12 {
        &--desktop-#{$start}-#{$end} {
          @include onDesktop {
            grid-column: #{$start} / #{$end + 1};
          }
        }
      }
    }
  }
}

sources.styles\\blocks\\icon.scss:
.icon {
  position: relative;

  display: block;
  width: 100%;
  padding-bottom: 100%;

  background-size: contain;
  background-position: center;
  background-repeat: no-repeat;
  text-decoration: none;

  &:hover {
    transition: transform 0.3s;
    transform: scale(1.2);
    cursor: pointer;
  }

  &__tooltip {
    display: none;

    @include onTablet {
      position: absolute;
      right: 100%;
      display: block;
      width: max-content;
      padding-right: 20px;
      top: 50%;
      transform: translateY(-50%);

      font-weight: 700;
      font-size: 12px;
      line-height: 16px;
      color: $primaryColor;
      letter-spacing: 2px;
      text-transform: uppercase;
      opacity: 0;
      pointer-events: none;

      transition: opacity 0.3s;
    }
  }

  &:hover &__tooltip {
    opacity: 1;
    pointer-events: all;
  }
  

  &--menu {
    background-image: url(../images/menu.svg);

    &:hover {
      background-image: url(../images/menu_hover.svg);
    }
  }

  &--phone {
    background-image: url(../images/phone.svg);
  }

  &--cross {
    background-image: url(../images/cross.svg);
  }
}

